package rs.etf.m2m.reporter.rest;

import java.util.ArrayList;
import java.util.List;
import java.util.stream.Collectors;

import javax.annotation.PostConstruct;
import javax.ejb.LocalBean;
import javax.ejb.Stateless;
import javax.inject.Inject;
import javax.persistence.EntityManager;
import javax.ws.rs.Consumes;
import javax.ws.rs.GET;
import javax.ws.rs.POST;
import javax.ws.rs.Path;
import javax.ws.rs.Produces;
import javax.ws.rs.core.MediaType;
import javax.ws.rs.core.Response;

import rs.etf.m2m.reporter.models.Report;
import rs.etf.m2m.reporter.models.utilities.EMPU;
import rs.etf.m2m.reporter.rest.beans.ReportRequest;

@Stateless
@Path("/report")
@Produces({ MediaType.APPLICATION_JSON })
@Consumes({ MediaType.APPLICATION_JSON })
public class ReportService {

	@Inject
	@EMPU
	EntityManager em;

	@POST
	public Response publish(ReportRequest reportRequest) throws Exception {
		Report report = Mapper.reportRequestToReport.apply(reportRequest);

		em.persist(report);

		return Response.ok().build();
	}

	@GET
	public Response getAll() throws Exception {
		List<Report> reports = em.createQuery("FROM Report", Report.class).getResultList();

		return Response.ok(reports.stream().map(Mapper.reportToReportRequest).collect(Collectors.toList())).build();
	}

	@PostConstruct
	public void init() {
		em.persist(Report.builder().comment("--No Comment--").latitude(23.35).longitude(23.35).photo(
				"data:image/jpeg;base64,")
				.build());
		em.persist(Report.builder().comment("--No Comment--").latitude(23.35).longitude(23.35).photo(
				"data:image/jpeg;base64,")
				.build());
		em.flush();
	}
}
